// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Math.sol";
import "./2_Pool.sol";
import "./3_Collateral.sol";
import "./LVRToken.sol";
import "./Conversion.sol";

contract LendingPool {
    using SafeMath for uint256;
    using Math for uint256;
    Conversion public conversion;

    event depositEvent(address _user, uint256 _quantity, address _asset);
    event borrowEvent(
        address _user,
        uint256 _borrowed,
        address _borrowedAsset,
        uint256 _collateral,
        address _collateralAsset
    );

    //public keyword??
    Pool public pool;
    Collateral public collateral;
    address owner;
    LVRToken public token;
    // right now only mapping to amount borrowed and in what currency

    mapping(address => mapping(address => uint256)) userColl;

    mapping(address => uint256) lenderTokenTracker;

    //array to track what asset addresses user borrowed
    mapping(address => address[]) userBorrowCurrencies;
    // account for double borrrow
    mapping(address => mapping(address => bool)) initializedBorrowCurrencyTracker;

    struct borrowAmountTracker {
        uint256 principal;
        uint256 interest; // need to update this through the contract
    } // update everything to fit this including below
    mapping(address => mapping(address => borrowAmountTracker))
        public borrowTracker;

    //track users
    address[] public borrowerAddresses;
    //mapping to make sure no double adding:
    mapping(address => bool) initializedBorrowerTracker;

    constructor() {
        pool = new Pool();
        token = new LVRToken("TestToken", "TST", address(pool));
        collateral = new Collateral(address(pool), address(token));
        conversion = new Conversion();
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function Deposit(uint256 _quantity, address _asset) public {
        // uint256 _borrowNow? why not bool?
        require(_quantity > 0, "deposit must be positive");
        uint256 tokenQuantity = token.mint(msg.sender, _quantity, _asset);
        lenderTokenTracker[msg.sender] += tokenQuantity;
        pool.depositTransfer(msg.sender, _asset, _quantity, false);

        // // borrowNow should be amount borrowed
        // if (_borrowNow > 0) {
        //     uint256 usedCollateral = _quantity - collateral.collReq()*_borrowNow;
        //     Borrow(_borrowNow, address)
        // }

        //emit depositEvent(msg.sender, _quantity, _asset);
    }

    function Withdraw(address _desiredAsset, uint256 _amountToRemove) public {
        uint256 assetPriceUSD = conversion.getPrice(_desiredAsset);

        //check if enough liquid to send
        require(
            _amountToRemove <= pool.getLiquidQuantity(_desiredAsset),
            "User cannot redeem more than the available balance"
        ); // future this will only check to see if we have any liquid - engine will swap currencies to allow user to withdraw anything they want.

        // total value of currency redeemable by user
        uint256 totalAssetValueFromOwnedTokens = ((token.balanceOf(msg.sender) *
            token.tokenDollarValue()) / assetPriceUSD); // Units: N token * USD/token / USD/currency = N currency

        // check that the user is allowed to redeem the amount
        // seems to be some precision errors
        require(
            (totalAssetValueFromOwnedTokens >= _amountToRemove),
            "Cannot redeem more tokens than number owned"
        );

        uint256 burnNumber = ((assetPriceUSD * _amountToRemove) /
            token.tokenDollarValue()); // Units: USD/currecny * N currency / (USD/token) = N token

        lenderTokenTracker[msg.sender] -= burnNumber;
        if (lenderTokenTracker[msg.sender] == 0) {
            delete lenderTokenTracker[msg.sender];
        }
        pool.withdrawTransfer(msg.sender, _desiredAsset, _amountToRemove);
        token.burn(msg.sender, burnNumber);
    }

    //this will have to also transfer LVR if they wont to borrow against that.
    // what about choosing collateral to borrrow against.
    function Borrow(
        uint256 _borrowQuantity,
        address _asset,
        uint256 _collateralQuantity,
        address _collateral
    ) public {
        // check if deposited collateral + outstanding collateral is enough to cover thier borrow request
        uint256 usdCollateralValue = (conversion.getPrice(_collateral) *
            _collateralQuantity) + collateral.collateralValue(msg.sender);
        uint256 usdBorrowValue = conversion.getPrice(_asset) *
            _borrowQuantity +
            totalValueBorrowed(msg.sender);

        require(
            usdCollateralValue >= usdBorrowValue * collateral.collReq(),
            "not enough collateral to borrow"
        );
        collateral.depositCollateral(
            msg.sender,
            _collateral,
            _collateralQuantity
        );
        pool.borrowTransfer(msg.sender, _asset, _borrowQuantity);

        emit borrowEvent(
            msg.sender,
            _borrowQuantity,
            _asset,
            _collateralQuantity,
            _collateral
        );
        // ledger here or in Pool contract?? there is one in collat, so maybe here just track amount borrowed

        borrowTracker[msg.sender][_asset].principal += _borrowQuantity;

        //only add asset ot user's array if it doesnt exist there already
        if (!initializedBorrowCurrencyTracker[msg.sender][_asset]) {
            userBorrowCurrencies[msg.sender].push(_asset);
            initializedBorrowCurrencyTracker[msg.sender][_asset] = true;
        }

        //only add to array if user doesnt exist already
        if (!initializedBorrowerTracker[msg.sender]) {
            borrowerAddresses.push(msg.sender);
            initializedBorrowerTracker[msg.sender] = true;
        }
    }

    // User repays loan by specifying the asset they are giving back and how much
    function Repay(address _asset, uint256 _repayQuantity) public {
        //cant return more than borrowed
        require(
            (borrowTracker[msg.sender][_asset].principal +
                borrowTracker[msg.sender][_asset].interest) >= _repayQuantity,
            "Returned more than borrowed"
        );

        pool.depositTransfer(msg.sender, _asset, _repayQuantity, true);
        //Then keep track of how much they owe and subtract that from collateral return?

        //remove principal first, then interest balance upose repayment
        // uint256 principal_portion = minimum(
        //     _repayQuantity,
        //     borrowTracker[msg.sender][_asset].principal
        // ); //  sub1 = min(repayment, principal)
        // borrowTracker[msg.sender][_asset].principal -= principal_portion; //  subtract that from principal so that it goes to zero
        // uint256 remainder = (_repayQuantity - principal_portion); //  remainder  = repayment - sub1
        // borrowTracker[msg.sender][_asset].interest -= remainder; //  subtract remainder from interest

        if (borrowTracker[msg.sender][_asset].principal == 0) {
            delete borrowTracker[msg.sender][_asset].principal;
        }
        if (borrowTracker[msg.sender][_asset].interest == 0) {
            delete borrowTracker[msg.sender][_asset].interest;
        }
    }

    function unlockCollateral(
        address _asset,
        uint256 _quantity,
        bool _toPool
    ) public {
        //check if they have loan value less than collat value/collatQtyReq
        require(
            (collateral.collateralValue(msg.sender) -
                ((conversion.getPrice(_asset) * _quantity) / (1 * 10**18))) >=
                collateral.collReq() * totalValueBorrowed(msg.sender),
            "Cannot withdraw collateral before paying back loan"
        );
        //integrate fees here- trasnfer collateral minus how much they owe us
        uint256 collateralDeposited = collateral.userCollateral(
            msg.sender,
            _asset
        );

        require(
            _quantity <= collateralDeposited,
            "Redeeming more collateral than deposited"
        );

        collateral.removeCollateral(msg.sender, _asset, _quantity, _toPool);
        if (_toPool == true) {
            uint256 tokenQuantity = token.mint(msg.sender, _quantity, _asset);
            lenderTokenTracker[msg.sender] += tokenQuantity;
        }
    }

    function accrueInterest() public {
        //uint256 numAssets = pool.assetsSupported().length;
        uint256 numUsers = borrowerAddresses.length;
        for (uint16 i = 0; i < numUsers; i++) {
            address userAddress = borrowerAddresses[i];
            uint256 numAssets = userBorrowCurrencies[userAddress].length;

            for (uint16 j = 0; j < numAssets; j++) {
                borrowTracker[userAddress][userBorrowCurrencies[userAddress][j]]
                    .interest += ((pool.InterestRate(
                    userBorrowCurrencies[userAddress][j]
                ) *
                    borrowTracker[userAddress][
                        userBorrowCurrencies[userAddress][j]
                    ].principal) / (1 * 10**18));
            }
            //better to have [asse][msg.sedner] instaed? makes finding individual's stats impossible
        }
    }

    function totalValueBorrowed(address _borrower)
        public
        view
        returns (uint256)
    {
        uint256 totalBorrowValue = 0;

        // make this less bits?
        uint256 numberAddresses = userBorrowCurrencies[_borrower].length;
        //loop to calculate the value of the pool liquid + borrowed
        for (uint16 i = 0; i < numberAddresses; i++) {
            totalBorrowValue =
                totalBorrowValue +
                (((borrowTracker[_borrower][userBorrowCurrencies[_borrower][i]]
                    .principal +
                    borrowTracker[_borrower][userBorrowCurrencies[_borrower][i]]
                        .interest) *
                    conversion.getPrice(userBorrowCurrencies[_borrower][i])) /
                    (1 * 10**18));
        }
        return totalBorrowValue;
    }

    function quantityBorrowed(address _user, address _asset)
        public
        view
        returns (uint256)
    {
        return borrowTracker[_user][_asset].interest;
    }

    /*
    how lending works: 
    -repaying loan involves returning borrowed asset quantity
    -interest still needs to be figured out; 
        -interst valued in USD and translated to coin?
        -percent of coin borrowed?
    -paying interest will be keeping a piece of thier collat
    -paying out to lenders will involve minting LVR and giving them lvr while adding asset to pool
    */

    function offerCollateralAssets() public {
        // just needs to check with the pool to see what assets we can offer
        //users to use as collateral - called when borrowing (if they deposit
        //LVR and want to use something else)
        // to do this we probably need to come up with some reserve rules, etc
    }

    function getInterestRate(address _asset) public view returns (uint256) {
        return pool.InterestRate(_asset);
    }

    function minimum(uint256 a, uint256 b) public pure returns (uint256) {
        return a.min(b);
    }

    function getLiquidity(address _token) public view returns (uint256) {
        return pool.getLiquidQuantity(_token);
    }

    function getPoolBalance(address _token) public view returns (uint256) {
        return pool.getBalance(_token);
    }

    function getLiquidPct(address _token) public view returns (uint256) {
        return pool.getLiquidPct(_token);
    }

    function getLockedCollateral(address _token) public view returns (uint256) {
        return collateral.getCollateralQuantity(_token);
    }

    function getCollBalance(address _token) public view returns (uint256) {
        return collateral.getBalance(_token);
    }

    function getPoolAddr() public view returns (address) {
        return address(pool);
    }

    function getCollAddr() public view returns (address) {
        return address(collateral);
    }

    function getTokenAddr() public view returns (address) {
        return address(token);
    }

    function getTokenDollarValue() public view returns (uint256) {
        return token.tokenDollarValue();
    }

    function getPoolValue() public view returns (uint256) {
        return token.totalPoolValue();
    }

    function getTotalSupply() public view returns (uint256) {
        return token.totalSupply();
    }

    function getBalanceOf(address _user) public view returns (uint256) {
        return token.balanceOf(_user);
    }

    function getCollateralValue(address _user) public view returns (uint256) {
        return collateral.collateralValue(_user);
    }

    function getOutstandingLoan(address _user, address _asset)
        public
        view
        returns (uint256)
    {
        return (borrowTracker[_user][_asset].principal +
            borrowTracker[_user][_asset].interest);
    }

    function getUserCollateral(address _user, address _asset)
        public
        view
        returns (uint256)
    {
        return collateral.userCollateral(_user, _asset);
    }
}
