    );

    //time to track last interest accruel
    uint256 interestTime = block.timestamp;

    //public keyword??
    Pool public pool;
    Collateral public collateral;
    address owner;
    LVRToken public token;
    // right now only mapping to amount borrowed and in what currency

    mapping(address => uint256) lenderTokenTracker;

    // //array to track what asset addresses have been borrowed
    // address[] BorrowedCurrencies;
