pragma solidity ^0.8.0;

//import "./ierc20token.sol";
//import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// derive this from OpenZeppelin's Ownable contract?
contract Collateral{
    //using SafeMath for uint256;

    // how to make this editable but only to the owner or something?
    // need to find a way to store as decimal
    uint256 public collReq = 2;

    struct Lockup{
        // probably need more info than this, add later
        // price functionality should be same as in reserve
        // uint256 price;
        uint256 lockQty;
    }

    mapping (address => Lockup) totalLock;
    mapping (address => mapping (address => uint256)) userColl;

    // i think we maybe don't need to say public here
    constructor() {
        collReq = 2;

        address LTC = 0x01BE23585060835E02B77ef475b0Cc51aA1e0709;
        totalLock[LTC] = Lockup(0);
    }

    function depositCollateral(address _borrower, address _token, 
                               uint256 _quantity) public {
        IERC20(_token).transferFrom(_borrower, address(this), _quantity);
        totalLock[_token].lockQty = totalLock[_token].lockQty + _quantity;
        userColl[_borrower][_token] = userColl[_borrower][_token] + _quantity;
    }

    function getCollateralQuantity(address _token) public view returns(uint256) {
        return totalLock[_token].lockQty;
    }

    function getBalance(address _token) public view returns(uint256) {
        return IERC20(_token).balanceOf(address(this));
    }


}
