pragma solidity ^0.8.0;

//import "./ierc20token.sol";
//import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// derive this from OpenZeppelin's Ownable contract?
contract Pool {
    //    using SafeMath for uint256;

    struct Reserve {
        // may need the USD price of these, which would have to update
        // uint256 price;
        // liquid quantity
        uint256 liqQty;
        // borrowed quantity
        uint256 borQty;
    }

    mapping(address => Reserve) public liquidity;

    address[] public addresses;

    // public?
    uint256 public totalPoolValue;
    // obviously this will also need to be a decimal - visibility?
    uint256 reserveReq;

    constructor() {
        // will need a function to update this
        reserveReq = 2;
        address LINK = 0x01BE23585060835E02B77ef475b0Cc51aA1e0709;
        // need the USD price probably
        liquidity[LINK] = Reserve(0, 0);
        addresses.push(LINK);
        // repeat this for all other tokens we support
        // how to add support for new tokens after deployment?
    }

    function depositTransfer(
        address _owner,
        address _token,
        uint256 _quantity
    ) public {
        // TODO: need to switch this to safemath? operations
        liquidity[_token].liqQty = liquidity[_token].liqQty + _quantity;
        IERC20(_token).transferFrom(_owner, address(this), _quantity);
    }

    // this should probably not be public
    function borrowTransfer(
        address _borrower,
        address _token,
        uint256 _quantity
    ) public {
        // can do transfer, not approve & transferFrom
        IERC20(_token).transfer(_borrower, _quantity);
        (liquidity[_token].liqQty -= _quantity);
        (liquidity[_token].borQty += _quantity);
    }

    function getLiquidQuantity(address _token) public view returns (uint256) {
        return liquidity[_token].liqQty;
    }

    function getTotalPool() public view returns (uint256) {
        // make this less bits?
        uint256 numberAddresses = addresses.length;
        // loop to calculate the value of the pool - needs conversion contract
        // for (uint16 i=0; i<numberAddresses; i++){
        //     totalPoolValue = totalPoolValue + liquidity[addresses[i]].liqQty * ]
        // }
    }

    // obviously will need to figure out the decimal situation here
    function getLiquidPct(address _token) public view returns (uint256) {
        // not working - returns the correct borrow quantity though
        return
            (1 * liquidity[_token].liqQty) /
            (liquidity[_token].liqQty + liquidity[_token].borQty);
        // return liquidity[_token].borQty;
    }

    function getBalance(address _token) public view returns (uint256) {
        return IERC20(_token).balanceOf(address(this));
    }
}
