pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Conversion.sol";
import "./2_Pool.sol";

// change to wadray math
// do we need IERC20 since its called by ERC20??

contract LVRToken is Context, AccessControlEnumerable, ERC20 {
    using SafeMath for uint256;
    Conversion public conversion;
    Pool public pool;

    //bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    //uint256 poolValue = 0; //poolValue should not be global varaible when pool exists

    constructor(
        string memory name,
        string memory symbol,
        address poolContractAddress
    ) ERC20(name, symbol) {
        //_setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        //_setupRole(MINTER_ROLE, address of deposit function or anything that will call mint);
        //_setupRole(MINTER_ROLE, _msgSender());
        conversion = new Conversion();
        pool = Pool(poolContractAddress);
    }

    function mint(
        address to,
        uint256 _quantity,
        address tokenContractAddress // could also be token contract address mapping
    ) public virtual {
        // require(
        //     hasRole(MINTER_ROLE, _msgSender()),
        //     "must have minter role to mint"
        // );
        uint256 usd_quantity = getTotalValue(tokenContractAddress, _quantity);
        uint256 amount = mintQuantity(usd_quantity);
        _mint(to, amount);
        //(poolValue += usd_quantity);
    }

    // number of tokens to mint- takes input of USD dollar value of deposit
    function mintQuantity(uint256 depositValue) public view returns (uint256) {
        uint256 poolValue = getPoolValue(); //poolValue will not be global varaible when pool exists
        if (totalSupply() == 0) {
            return depositValue;
        } else {
            return ((totalSupply() * depositValue) / poolValue);
        }
    }

    // gets total USD value of a currency and amount of currency desposited
    // depositQuantity comes from user specified amount
    function getTotalValue(
        address tokenContractAddress,
        uint256 depositQuantity
    ) public view returns (uint256) {
        uint256 token_price = conversion.getPrice(tokenContractAddress);
        uint256 usd_total_value = ((token_price * depositQuantity) /
            (1 * 10**18));
        return usd_total_value;
    }

    // returns token dollar value *10**18
    function tokenDollarValue() public view returns (uint256) {
        require(totalSupply() > 0, "supply is zero");
        return ((getPoolValue() * 10**18) / totalSupply()); // dollar value times 10**18: ie output of 12000... would be $1.20
    }

    function getPoolValue() public view returns (uint256) {
        // return pool value
        return pool.getTotalPool();
    }

    function burn(uint256 _amount) external {
        require(_amount > 0, "Amount to redeem needs to be > 0");
        require(balanceOf(msg.sender) >= _amount, "improper balance");
        //cumulates the balance of the user
        // (,
        // uint256 currentBalance,
        // uint256 balanceIncrease,
        // uint256 index) = cumulateBalanceInternal(msg.sender);

        uint256 amountToRedeem = _amount;

        // if amount is equal to uint(-1), the user wants to redeem everything
        // if (_amount == UINT_MAX_VALUE) {
        //     amountToRedeem = currentBalance;
        // }

        // require(
        //     amountToRedeem <= currentBalance,
        //     "User cannot redeem more than the available balance"
        // );

        //check that the user is allowed to redeem the amount
        // require(
        //     isTransferAllowed(msg.sender, amountToRedeem),
        //     "Transfer cannot be allowed."
        // );

        // burns tokens equivalent to the amount requested
        /* 
        will eventually have to pull from pool that contains a specified currency and reduce the currency pool by specific amount
            this will mean convert to a currency then pull that amount from pool
            so an imput parameter will have to be desired currency or token contract address depending on how this is applied
        */
        //(poolValue -= ((tokenDollarValue() * amountToRedeem) / 10**18));
        //this will have to burn tokens and pull from pool and send to msg.sender
        _burn(msg.sender, amountToRedeem);

        ////// this should be in another function that withdraws or repays
        ////// then those functions should call burn
        // executes redeem of the underlying asset
        // pool.redeemUnderlying(
        //     underlyingAssetAddress,
        //     msg.sender,
        //     amountToRedeem,
        //     currentBalance.sub(amountToRedeem)
        // );
    }
}
