    //////////////// function if we want to withdraw based on LVR token quanitity//////////////////
    function Withdraw(address _desiredAsset, uint256 _tokenQuantity) public {
        uint256 assetPriceUSD = conversion.getPrice(_desiredAsset);

        // total value of currency redeemable by user
        uint256 totalAssetFromTokens = ((_tokenQuantity *
            token.tokenDollarValue()) / assetPriceUSD); // Units: N token * USD/token / USD/currency = N currency

        //check if enough liquid to send
        require(
            totalAssetFromTokens <= pool.getLiquidQuantity(_desiredAsset),
            "User cannot redeem more than the available balance"
        ); // future this will only check to see if we have any liquid - engine will swap currencies to allow user to withdraw anything they want.

        // check that the user is allowed to redeem the amount
        require(
            (token.balanceOf(msg.sender) >= _tokenQuantity),
            "Cannot redeem more tokens than number owned"
        );

        lenderTokenTracker[msg.sender] -= _tokenQuantity;
        if (lenderTokenTracker[msg.sender] == 0) {
            delete lenderTokenTracker[msg.sender];
        }
        pool.withdrawTransfer(msg.sender, _desiredAsset, totalAssetFromTokens);
        token.burn(msg.sender, _tokenQuantity);
    }

    //this will have to also transfer LVR if they wont to borrow against that.
    // what about choosing collateral to borrrow against.
    function Borrow(
        uint256 _borrowQuantity,
        address _asset,
        uint256 _collateralQuantity,
        address _collateral
    ) public {
        // check if deposited collateral + outstanding collateral is enough to cover thier borrow request
        uint256 usdCollateralValue = (conversion.getPrice(_collateral) *
            _collateralQuantity) + collateral.collateralValue(msg.sender);
        uint256 usdBorrowValue = conversion.getPrice(_asset) *
            _borrowQuantity +
            totalValueBorrowed(msg.sender);

        require(
            usdCollateralValue >= usdBorrowValue * collateral.collReq(),
            "not enough collateral to borrow"
        );
        collateral.depositCollateral(
            msg.sender,
            _collateral,
            _collateralQuantity
        );
        pool.borrowTransfer(msg.sender, _asset, _borrowQuantity);

        emit borrowEvent(
            msg.sender,
            _borrowQuantity,
            _asset,
            _collateralQuantity,
            _collateral
        );

        borrowTracker[_asset][msg.sender].principal += _borrowQuantity;

        //only add asset if it doesnt exist there already
        if (!initializedBorrowedCurrency[_asset]) {
            BorrowedCurrencies.push(_asset);
            initializedBorrowedCurrency[_asset] = true;
        }

        //only add to array if user doesnt exist already
        //////////////////////////// might not need anymore
        if (!initializedBorrowerTracker[_asset][msg.sender]) {
            borrowersByAsset[_asset].push(msg.sender);
            initializedBorrowerTracker[_asset][msg.sender] = true;
        }
    }

    // User repays loan by specifying the asset they are giving back and how much
    function Repay(address _asset, uint256 _repayQuantity) public {
        //cant return more than borrowed
        require(
            (borrowTracker[_asset][msg.sender].principal +
                borrowTracker[_asset][msg.sender].interest) >= _repayQuantity,
            "Returned more than borrowed"
        );

        //remove principal first, then interest balance upose repayment
        uint256 principal_portion = minimum(
            _repayQuantity,
            borrowTracker[_asset][msg.sender].principal
        ); //  sub1 = min(repayment, principal)
        uint256 interest_portion = (_repayQuantity - principal_portion); //  interest_portion  = repayment - sub1
        //this after principal_portion and interest_portion so we can subtract this from brwQty in Pool.sol
        pool.repayTransfer(
            msg.sender,
            _asset,
            _repayQuantity,
            principal_portion,
            interest_portion
        );

        borrowTracker[_asset][msg.sender].principal -= principal_portion; //  subtract that from principal so that it goes to zero
        borrowTracker[_asset][msg.sender].interest -= interest_portion; //  subtract interest_portion from interest

        if (borrowTracker[_asset][msg.sender].principal == 0) {
            delete borrowTracker[_asset][msg.sender].principal;
        }
        if (borrowTracker[_asset][msg.sender].interest == 0) {
            delete borrowTracker[_asset][msg.sender].interest;
        }
    }

    function unlockCollateral(
        address _asset,
        uint256 _quantity,
        bool _toPool
    ) public {
        //check if they have loan value less than collat value/collatQtyReq
        require(
            (collateral.collateralValue(msg.sender) -
                ((conversion.getPrice(_asset) * _quantity) / (1 * 10**18))) >=
                collateral.collReq() * totalValueBorrowed(msg.sender),
            "Cannot withdraw collateral before paying back loan"
        );
        //integrate fees here?- trasnfer collateral minus how much they owe us
        uint256 collateralDeposited = collateral.userCollateral(
            _asset,
            msg.sender
        );

        require(
            _quantity <= collateralDeposited,
            "Redeeming more collateral than deposited"
        );

        collateral.removeCollateral(msg.sender, _asset, _quantity, _toPool);
        if (_toPool == true) {
            uint256 tokenQuantity = token.mint(msg.sender, _quantity, _asset);
            lenderTokenTracker[msg.sender] += tokenQuantity;
        }
    }

    function accrueInterest() public {
        uint256 numAssets = BorrowedCurrencies.length;
        for (uint16 i = 0; i < numAssets; i++) {
            address asset = BorrowedCurrencies[i];
            uint256 numBorrowers = borrowersByAsset[asset].length;
            uint256 assetInterestRate = pool.InterestRate(asset);
            for (uint16 j = 0; j < numBorrowers; j++) {
                borrowTracker[asset][borrowersByAsset[asset][j]]
                    .interest += ((assetInterestRate *
                    borrowTracker[asset][borrowersByAsset[asset][j]]
                        .principal) / (1 * 10**18));
            }
        }
    }

    function payoutInterest() public {
        /*
        Note:  asset is already in pool from repay
                -need to track the interest value
                -reset interst value to 0 upon payout
        How: when you repay, your principle is added to liquidity, and interst is stored in another variable
        upon transfer, token are minted interest is added to liduidity
        so in calculating pool value, liquidity shoudl be used, not balance of contract itself. 

        Pool:
        1) total amount of asset lent
        2) how much each person lent of that asset
        3) ratio of those (call it r1) is how much of interest they get

        For each person:
        1) list of lenders
        2) how much they lent in lVR
        3) current number of LVR
        4) ratio of those(call it r2) is what they get of thier cut
        5) multiply r1*r2 to get total asset receiving 
        6) mint LVR for the value of r1*r2


        for asset:
            t = total asset lent
            for lenders of [asset]:
                num= r2 * amount lent[lender]/t
                mint(user, num, asset)
         */
        // retainedTokenRation =
        //     token.getBalanceOf(lenders[i]) /
        //     lenderTokenTracker[lenders[i]];
    }

    function totalValueBorrowed(address _borrower)
        public
        view
        returns (uint256)
    {
        uint256 totalBorrowValue = 0;

        // make this less bits?
        //////uint256 numberAddresses = userBorrowCurrencies[_borrower].length;
        uint256 numAssets = BorrowedCurrencies.length;

        //loop to calculate the value of the pool liquid + borrowed
        for (uint16 i = 0; i < numAssets; i++) {
            address asset = BorrowedCurrencies[i];
            uint256 assetPrice = conversion.getPrice(asset);
            totalBorrowValue += (((borrowTracker[asset][_borrower].principal +
                borrowTracker[asset][_borrower].interest) * assetPrice) /
                (1 * 10**18));
        }
        return totalBorrowValue;
    }

    function quantityBorrowed(address _user, address _asset)
        public
        view
        returns (uint256)
    {
        return borrowTracker[_asset][_user].interest;
    }

    /*
    how lending works: 
    -repaying loan involves returning borrowed asset quantity
    -interest still needs to be figured out; 
        -interst valued in USD and translated to coin?
        -percent of coin borrowed?
    -paying interest will be keeping a piece of thier collat
    -paying out to lenders will involve minting LVR and giving them lvr while adding asset to pool
