contract LendingPool {
    using SafeMath for uint256;
    using Math for uint256;
    Conversion public conversion;

    event depositEvent(address _user, uint256 _quantity, address _asset);
    event borrowEvent(
        address _user,
        uint256 _borrowed,
        address _borrowedAsset,
        uint256 _collateral,
        address _collateralAsset
    );

    //public keyword??
    Pool public pool;
    Collateral public collateral;
    address owner;
    LVRToken public token;
    // right now only mapping to amount borrowed and in what currency

    mapping(address => uint256) lenderTokenTracker;

    //array to track what asset addresses have been borrowed
    address[] BorrowedCurrencies;
    // account for double borrrow
    mapping(address => bool) initializedBorrowedCurrency;

    address[] lentCurrencies;
    // account for double borrrow
    mapping(address => bool) initializedLentCurrency;

    struct borrowAmountTracker {
        uint256 principal;
        uint256 interest;
    }
    //asset -> user -> amount
    mapping(address => mapping(address => borrowAmountTracker))
        public borrowTracker;

    //asset -> user -> amount lent
    mapping(address => mapping(address => uint256)) LentAmountTracker;

    //asset -> list of borrowers
    mapping(address => address[]) borrowersByAsset;
    //mapping to make sure no double adding:
    mapping(address => mapping(address => bool)) initializedBorrowerTracker;

    //asset -> list of borrowers
    mapping(address => address[]) lendersByAsset;
    //mapping to make sure no double adding:
    mapping(address => mapping(address => bool)) initializedLenderTracker;

    constructor() {
        pool = new Pool();
        token = new LVRToken("TestToken", "TST", address(pool));
        collateral = new Collateral(address(pool), address(token));
        conversion = new Conversion();
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function Deposit(uint256 _quantity, address _asset) public {
        // uint256 _borrowNow? why not bool?
        require(_quantity > 0, "deposit must be positive");
        uint256 tokenQuantity = token.mint(msg.sender, _quantity, _asset);
        lenderTokenTracker[msg.sender] += tokenQuantity;
        pool.depositTransfer(msg.sender, _asset, _quantity);

        if (!initializedLentCurrency[_asset]) {
            lentCurrencies.push(_asset);
            initializedLentCurrency[_asset] = true;
        }
        if (!initializedLenderTracker[_asset][msg.sender]) {
            lendersByAsset[_asset].push(msg.sender);
            initializedLenderTracker[_asset][msg.sender] = true;
        }
        LentAmountTracker[_asset][msg.sender] += _quantity;

        // // borrowNow should be amount borrowed
        // if (_borrowNow > 0) {
        //     uint256 usedCollateral = _quantity - collateral.collReq()*_borrowNow;
        //     Borrow(_borrowNow, address)
        // }

        //emit depositEvent(msg.sender, _quantity, _asset);
    }

